<style>	
@font-face {
	font-family : 'Bandal';
	src : url('Bandal.woff') format('woff'), url('Bandal.svg#Bandal') format('dsvg');
	font-weight : normal;
	font-style : normal;
	font-stretch : normal;
}

body {
	background-color : #eeffff;
	font-family : 'Alice', "Hiragino Mincho ProN", "メイリオ", "Ume Mincho S3", serif;
	margin : 0px;
}
.txt {
	margin : 50px;
	padding : 50px;
	background-color : white;
/*	font-family : "Hiragino Mincho ProN", "メイリオ", "Ume Mincho S3", serif;
